package main

import (
"fmt"
)

func main() {
	fmt.Printf("%v\n", generatePalindromes(""))
}

func generatePalindromes(s string) []string {
	mapping := make(map[byte]int, 0)
	result := make(map[string]bool, 0)

	for _, b := range s {
		mapping[byte(b)] += 1
	}

	var chosen string
	count := 0
	for k, v := range mapping {
		if v%2 != 0 {
			chosen = string(k)
			count++
		}
	}
	if count > 1 {
		return []string{}
	}
	fmt.Printf("mapping: %+v\n", mapping)

	helper("  ", mapping, s, chosen, &result)

	res := make([]string, 0)

	for ch, _ := range result {
		res = append(res, ch)
	}
	return res
}

func helper(path string, mapping map[byte]int, s string, chosen string, result *map[string]bool) {
	//fmt.Printf("%v mapping:%v chosen:%v \n", path, mapping, chosen)

	if len(s) == len(chosen) {
		(*result)[chosen] = true
		return
	} else {
		for ch, _ := range mapping {
			if mapping[ch] > 1 {
				mapping[ch] -= 2
				helper(path+"  ", mapping, s, string(ch)+chosen+string(ch), result)
				mapping[ch] += 2
			}
		}
	}
}

